Controller.content:
using System.Collections.Generic;
using System.Threading.Tasks;
using Microsoft.AspNetCore.Mvc;
using BankingApp.Domain.Entities;
using BankingApp.Services.Interfaces;

namespace BankingApp.Controllers
{
    [ApiController]
    [Route("api/[controller]")]
    public class AccountController : ControllerBase
    {
        private readonly IAccountService _accountService;

        public AccountController(IAccountService accountService)
        {
            _accountService = accountService;
        }

        [HttpGet]
        public async Task<ActionResult<IEnumerable<AccountRecord>>> GetAllAccounts()
        {
            var accounts = await _accountService.GetAllAccountsAsync();
            return Ok(accounts);
        }

        [HttpGet("{accountNumber}")]
        public async Task<ActionResult<AccountRecord>> GetAccountByNumber(long accountNumber)
        {
            var account = await _accountService.GetAccountByNumberAsync(accountNumber);
            if (account == null)
            {
                return NotFound();
            }
            return Ok(account);
        }

        [HttpPost]
        public async Task<ActionResult> AddAccount([FromBody] AccountRecord account)
        {
            await _accountService.AddAccountAsync(account);
            return CreatedAtAction(nameof(GetAccountByNumber), new { accountNumber = account.AccountNumber }, account);
        }

        [HttpPut("{accountNumber}")]
        public async Task<ActionResult> UpdateAccount(long accountNumber, [FromBody] AccountRecord account)
        {
            if (accountNumber != account.AccountNumber)
            {
                return BadRequest();
            }
            await _accountService.UpdateAccountAsync(account);
            return NoContent();
        }

        [HttpDelete("{accountNumber}")]
        public async Task<ActionResult> DeleteAccount(long accountNumber)
        {
            await _accountService.DeleteAccountAsync(accountNumber);
            return NoContent();
        }
    }
}

DbContext.content:
using Microsoft.EntityFrameworkCore;
using BankingApp.Domain.Entities;

namespace BankingApp.Data
{
    public class ApplicationDbContext : DbContext
    {
        public ApplicationDbContext(DbContextOptions<ApplicationDbContext> options)
            : base(options)
        {
        }

        public DbSet<AccountRecord> Accounts { get; set; }

        protected override void OnModelCreating(ModelBuilder modelBuilder)
        {
            base.OnModelCreating(modelBuilder);
            modelBuilder.Entity<AccountRecord>().ToTable("ACCOUNTS");
        }
    }
}

Program.content:
using Microsoft.AspNetCore.Builder;
using Microsoft.EntityFrameworkCore;
using Microsoft.Extensions.DependencyInjection;
using Microsoft.Extensions.Hosting;
using BankingApp.Data;
using BankingApp.Repositories;
using BankingApp.Repositories.Interfaces;
using BankingApp.Services;
using BankingApp.Services.Interfaces;

var builder = WebApplication.CreateBuilder(args);

// Add services to the container
builder.Services.AddControllers();

// Configure Entity Framework with MySQL
var connectionString = builder.Configuration.GetConnectionString("DefaultConnection");
builder.Services.AddDbContext<ApplicationDbContext>(options =>
    options.UseMySql(connectionString, ServerVersion.AutoDetect(connectionString)));

// Register repositories and services
builder.Services.AddScoped<IAccountRepository, AccountRepository>();
builder.Services.AddScoped<IAccountService, AccountService>();

var app = builder.Build();

// Configure the HTTP request pipeline
if (app.Environment.IsDevelopment())
{
    app.UseDeveloperExceptionPage();
}

app.UseHttpsRedirection();
app.UseAuthorization();
app.MapControllers();

// Ensure database is created
using (var scope = app.Services.CreateScope())
{
    var dbContext = scope.ServiceProvider.GetRequiredService<ApplicationDbContext>();
    dbContext.Database.EnsureCreated();
}

app.Run();
